mapper.select:
SELECT bno, title, content, regdate, viewcnt, m.memID
FROM board b, member m
WHERE b.memID = m.memID and bno = #{bno}

SELECT * FROM (
    SELECT rownum as rn, A.* from(
        SELECT rownum, bno, title, regdate, viewcnt, m.memID, 
			(SELECT count(*) FROM reply WHERE bno = b.bno) as cnt
        FROM board b, member m

        ORDER BY bno desc, regdate desc
    ) A
) WHERE rn between #{start} and #{end}

mapper.update:
UPDATE board
SET viewcnt = viewcnt + 1
WHERE bno = #{bno}

UPDATE board
SET title = #{map.title}, content = #{map.content} 
WHERE bno = #{map.bno}
