mapper.select:
SELECT * 
FROM (
    SELECT rownum as rn, m.* 
    FROM (
        SELECT * 
        FROM member
    ) m
)
WHERE rn between #{start} and #{end}

SELECT count(*) 
FROM member 
WHERE memPW = #{memPW}

mapper.update:
UPDATE member
SET memID = #{memID}, memPW = #{memPW}, memEMAIL = #{memEMAIL}, memPHONE1 = #{memPHONE1}, memPHONE2 = #{memPHONE2}, memPHONE3 = #{memPHONE3}
WHERE memID = #{memID}

mapper.select:
SELECT * FROM (
    SELECT rownum as rn, A.* FROM
	    (
	        SELECT rownum, memID, memPW, memEMAIL, memPHONE1, memPHONE2, memPHONE3
	        FROM member m
	        ORDER BY rownum desc
	    ) A
) WHERE rn between #{start} and #{end}
